mapper.update:
UPDATE PARTNER
SET PARTNERCHECK=1
WHERE PARTNERNUM=#{partnerNum}

UPDATE PARTNER
SET PARTNERCHECK=0
WHERE PARTNERNUM=#{partnerNum}

UPDATE PARTNER 
SET PARTNEREMAIL=#{partnerEmail}, PARTNERACCOUNT=#{partnerAccount},
PARTNERNAME=#{partnerName}, COMPANYNAME=#{companyName},
PARTNERPHONE=#{partnerPhone}
WHERE PARTNERNUM=#{partnerNum}
