mapper.select:
SELECT QUESTIONNUM, PRODUCTNUM, ID, QUESTIONKIND, QUESTIONCONTENTS, QUESTIONDATE
  FROM QUESTION
 WHERE QUESTIONNUM = #{questionNum}

mapper.update:
UPDATE QUESTION
 SET
  QUESTIONKIND=#{questionKind},
  QUESTIONDATE = SYSDATE,
  QUESTIONCONTENTS = #{questionContents}
 WHERE QUESTIONNUM = #{questionNum}
